local ansicolor_data = [[]]

local ansicolor_module,
    err =
    load(
    ansicolor_data:gsub(
        "..",
        function(s)
            return string.char(tonumber(s, 16))
        end
    )
)

if err then
    print(err)
else
    local colors = ansicolor_module()

    print(colors("%{reset}%{yellow}Hello %{blue}world%{white}!"))
end
